#import "StyleKitName.h"


@implementation StyleKitName

#pragma mark Initialization

+ (void)initialize
{
}

#pragma mark Drawing Methods

+ (void)drawStopButton:(CGPoint)location;
{
    //// Color Declarations
    UIColor* color = [UIColor colorWithRed: 1 green: 0 blue: 0 alpha: 1];

    //// Oval Drawing
    UIBezierPath* ovalPath = [UIBezierPath bezierPathWithOvalInRect: CGRectMake(location.x, location.y, 35, 35)];
    [color setStroke];
    ovalPath.lineWidth = 2.5;
    [ovalPath stroke];
    

    //// Rectangle Drawing
    UIBezierPath* rectanglePath = [UIBezierPath bezierPathWithRect: CGRectMake(location.x+13, location.y+8, 10, 10)];
    [color setFill];
    [rectanglePath fill];
}

@end
